function AGDD_errorPlacement(e,s){e.addClass("help-block");let o=s.parents("div.form-group"),r=s.next("span");if(o.addClass("has-feedback"),"checkbox"===s.prop("type"))e.insertAfter(s.parent("label"));else{let o=s.parent("div.input-group");o.length>0?e.insertAfter(o):r.length>0?e.insertAfter(r):e.insertAfter(s)}o.append($("<span class='glyphicon glyphicon-remove form-control-feedback'></span>"))}function AGDD_success(e,s){$(s).parents("div.form-group").append($("<span class='glyphicon glyphicon-ok form-control-feedback'></span>"))}function AGDD_highlight(e,s,o){let r=$(e).parents("div.form-group");r.addClass("has-error").removeClass("has-success"),r.find(".glyphicon.form-control-feedback").addClass("glyphicon-remove").removeClass("glyphicon-ok")}function AGDD_unhighlight(e,s,o){let r=$(e).parents("div.form-group");r.addClass("has-success").removeClass("has-error"),r.find(".glyphicon.form-control-feedback").addClass("glyphicon-ok").removeClass("glyphicon-remove")}